body.raw:
{
  "default_title": "MyShop 官方商城",
  "default_description": "默认站点描述...",
  "default_keywords": "myshop, shop, ecommerce",
  "default_og_image": "/uploads/seo/default-og.jpg"
}

{
  "whatsapp": {
    "enabled": true,
    "phone": "+60123456789",
    "default_message": "Hi, I would like to ask about your products."
  }
}

{
  "key": "order_paid_email",
  "channel": "email",
  "name": "Order Paid Email",
  "subject_template": "Order {{order_number}} paid",
  "body_template": "Thanks for your order {{customer_name}}",
  "variables": {
    "order_number": "string",
    "customer_name": "string"
  },
  "is_active": true
}

{
  "subject_template": "Low stock alert",
  "body_template": "Stock for {{product_name}} is low.",
  "variables": {
    "product_name": "string"
  },
  "is_active": false
}

{
  "text": "Special promotion! Get 20% off on all products today!",
  "start_at": "2025-01-01",
  "end_at": "2025-12-31",
  "is_active": 1
}
